		public static byte[] IntToBytes(int intVal) 
		{   
			byte[] result = new byte[4];   
			result[3] = (byte)((intVal >> 24) & 0xFF);
			result[2] = (byte)((intVal >> 16) & 0xFF);
			result[1] = (byte)((intVal >> 8) & 0xFF); 
			result[0] = (byte)(intVal & 0xFF);
			return result;
		}
