		public static byte[] StreamToBytes(Stream stream) 
		{ 
			byte[] bytes = new byte[stream.Length]; 
			stream.Read(bytes, 0, bytes.Length); 

			stream.Seek(0, SeekOrigin.Begin); 		// 设置当前流的位置为流的开始 
			return bytes; 
		} 

